import React, { useState } from "react";
import gql from "graphql-tag";
import { Query, graphql } from "react-apollo";
import { Link, Router } from "../routes";
import Spinner from "./views/Spinner";
import Error from "./views/Error";
import Login from "./views/Login";
import LoginPortal from "./views/LoginPortal";

const Navbar = ({ handleClick }) => {
  let textInput;
  const GET_USER = gql`
    {
      getUsers {
        _id
        username
        password
      }
    }
  `;
  const handleLogin = async (client, username, password, users) => {
    await localStorage.setItem("usuario", username.value);
    await localStorage.setItem("password", password.value);
    await client.resetStore();
  };

  const [display, setDisplay] = useState(true);
  const [show, setShow] = useState(true);
  const handleSubmit = e => {
    e.preventDefault();
    Router.pushRoute("producto", { tag: textInput.value });
  };

  const handleLogout = client => {
    localStorage.removeItem("usuario");
    localStorage.removeItem("password");
    client.resetStore();
  };

  return (
    <React.Fragment>
      <div className="navbar-container">
        <div className="navbar">
          <div className="main-navbar">
            <div className="left-items">
              <div className="menu">
                <img
                  onClick={e => {
                    handleClick();
                  }}
                  className="icon-menu"
                  width="30px"
                  src="/static/icons/menu.svg"
                  alt="menu"
                />
              </div>

              <div className="logo">
                <Link route="/">
                  <a>
                    <img
                      className="main-navbar__title"
                      src="/static/logo2.webp"
                      width="100%"
                      alt=""
                    />
                  </a>
                </Link>
              </div>
            </div>

            <nav className="main-navbar__items">
              <ul>
                <li>
                  <Link route="producto" params={{ tag: "hombre" }}>
                    <a>Hombres</a>
                  </Link>
                </li>
                <li>
                  <Link route="producto" params={{ tag: "mujer" }}>
                    <a>Mujeres</a>
                  </Link>
                </li>
                <li>
                  <Link route="producto" params={{ tag: "niÃ±o" }}>
                    <a>NiÃ±os</a>
                  </Link>
                </li>
                <li>
                  <Link route="producto" params={{ tag: "" }}>
                    <a>Accesorios</a>
                  </Link>
                </li>
                <li>
                  <Link route="producto" params={{ tag: "" }}>
                    <a>Regalos</a>
                  </Link>
                </li>
              </ul>
            </nav>

            <div className="right-items">
              <div className="search">
                <span className="main-navbar__icon">
                  <form
                    onSubmit={e => {
                      handleSubmit(e);
                    }}
                  >
                    <input
                      type="search"
                      onFocus={() => {
                        setDisplay(false);
                      }}
                      ref={input => (textInput = input)}
                      onBlur={() => {
                        setDisplay(true);
                      }}
                      name="search"
                      id="search"
                      placeholder="Search"
                    />
                    <img
                      className="search-icon"
                      src="/static/icons/icons8-search.svg"
                      width="30px"
                      alt="search"
                    />
                  </form>
                </span>
              </div>

              <div className="login">
                <Query query={GET_USER}>
                  {({ client, loading, error, data }) => {
                    if (loading) return <h3>login</h3>;
                    if (error) return <Error code="502" />;
                    return (
                      <div className="login">
                        <LoginPortal
                          client={client}
                          setShow={setShow}
                          show={show}
                          users={() => data.getUsers}
                          handleLogin={handleLogin}
                        ></LoginPortal>

                        {!data.getUsers ? (
                          <h3
                            onClick={() => {
                              setShow(true);
                            }}
                          >
                            login
                          </h3>
                        ) : (
                          <div
                            className="logged"
                            onClick={() => {
                              handleLogout(client);
                            }}
                          >
                            <Login username={data.getUsers.username}></Login>
                          </div>
                        )}
                      </div>
                    );
                  }}
                </Query>
              </div>
            </div>
          </div>
        </div>
      </div>

      <style jsx>
        {`
          p {
            font-size: 0.7rem;
            text-decoration: underline;
          }
          .navbar-container {
            position: relative;
            width: 100%;
            height: 60px;
            box-sizing: border-box;
            overflow: hidden;
          }
          .navbar {
            width: 100%;
            background: white;
            position: fixed;
            z-index: 1000;
            box-sizing: border-box;
            -webkit-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.1);
            -moz-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.1);
            box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.1);
            z-index: 100;
          }
          .navbar h1 {
            margin: 0.5rem 0;
          }
          .main-navbar {
            margin: 0 auto;
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 0.5rem 1rem;
          }

          .left-items {
            display: flex;
            align-items: center;
            justify-content: flex-start;
          }
          .right-items {
            display: flex;
            align-items: center;
            flex-shrink: 1;
            width: 100%;
          }
          .logo {
            display: ${display ? "flex" : "none"};
            align-items: flex-end;
            height: auto;
          }

          //   search
          .search {
            flex-shrink: 1;
            width: 100%;
          }
          form {
            display: flex;
            align-items: center;
            width: 100%;
            justify-content: flex-end;
            margin: 0 0.8em;
          }
          #search {
            padding: 0.6rem;
            border: 0;

            width: 100%;
            border-radius: 15px;
            background: #dee8ef;
            transition: 0.5s;
          }
          #search::placeholder {
            color: grey;
          }
          .search-icon {
            display: ${display ? "inline-block" : "none"};
          }
          .main-navbar__items {
            display: none;
          }
          .main-navbar__items li:hover a {
            color: #fd486b;
          }

          .main-navbar__icon {
            display: flex;
            align-item: center;
            justify-content: center;
            flex-grow: 1;
          }
          .main-navbar__title {
            color: #e84855;
            font-size: 1.2rem;
            width: 50px;
          }
          .categorias {
            display: flex;
            align-item: center;
            padding-right: 1rem;
          }
          .icon-menu {
            color: red;
            transition-property: width, opacity;
            transition-duration: 1s, 0s;
            display: ${display ? "block" : "none"};
          }

          //login
          .login {
            display: ${display ? "block" : "none"};
          }
          .logged {
            display: flex;
            align-items: flex-end;
            flex-shrink: 1;
            width: 100%;
          }

          @media (min-width: 860px) {
            .logo {
              display: flex;
              width: 250px;
            }
            .right-items {
              flex-shrink: initial;
              width: 250px;
            }
            .menu {
              display: none;
            }

            .main-navbar__icon {
              flex-grow: 0;
            }
            .main-navbar__items {
              display: block;
            }
            #search {
              width: 170px;
            }
            .search-icon {
              display: none;
            }
            .main-navbar__items ul {
              display: flex;
              width: 460px;
              padding: 0;
              margin: 0;
              justify-content: space-between;
              align-items: center;
              height: 2rem;
            }
            .main-navbar__items a {
              font-size: 1.5em;
              color: black;
              letter-spacing: -1px;
            }
            .main-navbar__items ul li {
              list-style: none;
              font-size: 0.7rem;
            }
            .main-navbar__title {
              font-size: 1.7rem;
              width: 65px;
            }
            .login {
              display: block;
            }
          }
        `}
      </style>
    </React.Fragment>
  );
};
export default Navbar;
