import React from "react";

import "isomorphic-fetch";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import ProductosInCart from "../component/productViews/ProductosInCart";
import Layout from "../component/Layout";
import Error from "../component/views/Error";
import Spinner from "../component/views/Spinner";
import { Router } from "../routes";
const GET_USER = gql`
  {
    getUsers {
      _id
      username
      bag {
        _id
        title
        image
        price
        tags
        discount
      }
    }
  }
`;
class Cart extends React.Component {
  render() {
    return (
      <Layout>
        <div className="main">
          <h2>Carrito de compras </h2>
          <div className="container">
            <div className="container-products">
              <Query query={GET_USER}>
                {({ client, loading, error, data }) => {
                  if (loading) return <Spinner />;
                  if (error) return Router.pushRoute("/");
                  return (
                    <ProductosInCart
                      data={data.getUsers.bag}
                      client={client}
                      user={data.getUsers}
                    />
                  );
                }}
              </Query>
            </div>

            <div className="cart-boleta">boleta</div>
          </div>
        </div>
        <style jsx>
          {`
            .main {
              margin-top: 50px;
            }
            .container {
              margin-top: 50px;
              display: flex;
              flex-direction: column;
            }
            .container-products {
              flex-shrink: 1;
            }
            .cart-boleta {
              height: 100%;
              width: 100%;
              max-width: 50px;
            }
          `}
        </style>
      </Layout>
    );
  }
}
export default Cart;
