import React from "react";

import "isomorphic-fetch";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import ProductosInCart from "../component/productViews/ProductosInCart";
import Layout from "../component/Layout";
import Error from "../component/views/Error";
import Spinner from "../component/views/Spinner";
import { Router } from "../routes";
const GET_USER = gql`
  {
    getUsers {
      _id
      username
      bag {
        _id
        title
        image
        price
        tags
        discount
      }
    }
  }
`;
class Cart extends React.Component {
  render() {
    return (
      <Layout>
        <div className="main">
          <h2>Carrito de compras </h2>

          <Query query={GET_USER}>
            {({ client, loading, error, data }) => {
              if (loading) return <Spinner />;
              if (error) return Router.pushRoute("/");
              return (
                <div className="container">
                  <div className="container-products">
                    <ProductosInCart
                      data={data.getUsers.bag}
                      client={client}
                      user={data.getUsers}
                    />
                  </div>
                  <div className="cart-boleta">
                    <h3>Boleta</h3>
                    <ul>
                      {data.getUsers.bag.map(e => {
                        return (
                          <li className="cart-boleta__product" key={e._id}>
                            <span>{e.title.toLowerCase()}</span>
                            <span>{e.price}</span>
                          </li>
                        );
                      })}
                    </ul>
                  </div>
                </div>
              );
            }}
          </Query>
        </div>

        <style jsx>
          {`
            .main {
              margin-top: 50px;
            }
            .container {
              margin: 0 auto;
              max-width: 1200px;
              margin-top: 50px;
              display: flex;

              flex-direction: column;
            }
            .container-products {
              width: 100%;
            }
            .cart-boleta {
              height: 100%;
              width: 100%;
              felx-shrink: 1;
              max-width: 350px;
              margin-left: 70px;
            }
            ul {
              margin: 0;
              padding: 0;
            }
            .cart-boleta__product {
              display: flex;
              justify-content: space-between;
            }
            @media (min-width: 660px) {
              .container {
                flex-direction: row;
              }
            }
          `}
        </style>
      </Layout>
    );
  }
}
export default Cart;
