import React, { useState } from 'react';
import gql from 'graphql-tag';
import { Query, graphql } from 'react-apollo';
import { Link, Router } from '../routes';

const Navbar = ({ handleClick }) => {
	let textInput;
	const [
		display,
		setDisplay
