				.navbar-container {
					position: relative;
					width: 100%;
					height: 60px;
					box-sizing: border-box;
					overflow: hidden;
				}
				.navbar {
					width: 100%;
					background: white;
					position: fixed;
					z-index: 1000;
					box-sizing: border-box;
					-webkit-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.1);
					-moz-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.1);
					box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.1);
					z-index: 100;
				}
				.navbar h1 {
					margin: .5rem 0;
				}
				.main-navbar {
					margin: 0 auto;
					display: flex;
					justify-content: space-between;
					align-items: center;
					padding: .5rem 1rem;
				}
				form {
					display: flex;
